// Issue of Verified financial products
// Offer issues to liquidity pools for IoIs, fix pricing, make allotments, draw down capital, create subscriptions
// (c) Kallol Borah, Verified Network, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/Bond.sol";
import "./interfaces/Share.sol";
import "./interfaces/LP.sol";
import "./interfaces/ViaCash.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/VerifiedIssues.sol";
import "./interfaces/VerifiedProducts.sol";
import "./interfaces/IMarketMaker.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/ISecuritiesRegistry.sol";
import "./utilities/BokkyPooBahsDateTimeLibrary.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";

contract Issues is VerifiedIssues, Initializable {

    using BokkyPooBahsDateTimeLibrary for uint;

    bytes32 productRef;
    bytes32 category;
    address owner;
    address issuer;
    address registrar;
    address security;
    address issue;
    address productContract;
    address securityContract;
    address securityRegistriesContract;
    address viaFactoryContract;
    uint256 timeOfIssue;    

    address[] private platforms;

    address[] private currencies;

    address[] private investors;

    address[] private beneficiaries;

    //mapping of asked currency to issue details
    mapping(address => Share.currencySpecificStockAttribs) private shares;
    Share.commonStockAttribs stockIssue;

    //mapping of asked currency to issue details
    mapping(address => Bond.currencySpecificBondAttribs) private bonds;
    Bond.commonBondAttribs bondIssue;

    //mapping of currencies to investors in pools 
    mapping(bytes32 => ioi[]) private subscribers;

    //mapping of beneficiaries to payouts
    mapping(address => VerifiedIssues.payment[]) private payouts;

    event issued (
        uint256 issueSize,
        uint256 offerPrice, 
        uint256 minAskPrice,   
        uint256 minSubscription,
        bytes32 currency,
        address security,
        bytes32 offerType,
        bytes32 isin,
        bytes32 status,
        bytes32 productCategory
    );

    event offered(address security, bytes32 status);

    event opened(address security, bytes32 status);

    event closed(address security, bytes32 status);

    event allotted(address security, bytes32 status);
    
    function initialize(bytes32 _productReference, 
                        bytes32 _productCategory, 
                        address _issuer, 
                        address _registrar, 
                        address _owner,
                        address _securityContract, 
                        address _securityRegistriesContract,
                        address _viaFactoryContract
                        ) public initializer{
        owner = _owner;
        productRef = _productReference;
        category = _productCategory;
        issuer = _issuer;
        registrar = _registrar;
        productContract = msg.sender;
        securityContract = _securityContract;
        securityRegistriesContract = _securityRegistriesContract;
        viaFactoryContract = _viaFactoryContract;
    }

    function getOwner() external view returns(address){
        require(msg.sender==securityRegistriesContract);
        return owner;
    }

    function issueShare(uint256 _issueSize,
                        uint256 _offerPrice, 
                        uint256 _minAskPrice, 
                        uint256 _minSubscription,
                        bytes32 _currency,
                        bytes32 _offerType,
                        bytes32 _isin,
                        string calldata _offeringDocuments
                        ) external {
        require(registrar==msg.sender);
        require(category=="Stock");
        require(VerifiedProducts(productContract).getIssue(productRef)==address(this));
        bool found = false;
        address currencyAddress = ViaFactory(viaFactoryContract).getTokenByNameType(_currency,"ViaCash");
        require(ViaFactory(viaFactoryContract).getType(currencyAddress) == "ViaCash");
        for(uint i=0; i<currencies.length; i++){
            if(currencies[i]==currencyAddress)
                found = true;
        }
        if(!found)
            currencies.push(currencyAddress);
        shares[currencyAddress].issueSize = _issueSize;
        shares[currencyAddress].offerPrice = _offerPrice;
        shares[currencyAddress].minAskPrice = _minAskPrice;
        shares[currencyAddress].minSubscription = _minSubscription;
        shares[currencyAddress].currency = _currency;
        stockIssue.offerType = _offerType;
        stockIssue.isin = _isin;        
        stockIssue.offeringDocuments = _offeringDocuments;  
        stockIssue.status = "Issued";    
        emit issued (
            _issueSize,
            _offerPrice, 
            _minAskPrice,   
            _minSubscription,
            _currency,
            address(this),
            _offerType,
            _isin,
            "Issued",
            "Stock"
        );  
    }

    function getShare() external view returns(Share.currencySpecificStockAttribs[] memory, Share.commonStockAttribs memory){
        Share.currencySpecificStockAttribs[] memory stocks = new Share.currencySpecificStockAttribs[](currencies.length);
        Share.currencySpecificStockAttribs memory share;
        for(uint i=0; i<currencies.length; i++){
            share = Share.currencySpecificStockAttribs({
                issueSize : shares[currencies[i]].issueSize,
                faceValue : shares[currencies[i]].faceValue,
                offerPrice : shares[currencies[i]].offerPrice, 
                minAskPrice : shares[currencies[i]].minAskPrice,  
                minSubscription : shares[currencies[i]].minSubscription,
                currency : shares[currencies[i]].currency,
                security : shares[currencies[i]].security
            });
            stocks[i] = share;
        }
        return (stocks, stockIssue);
    }

    function issueBond(uint256 _issueSize,
                        uint256 _offerPrice, 
                        uint256 _minAskPrice, 
                        uint256 _minSubscription,
                        uint256 _couponPaymentCycle, 
                        uint256 _tenure,
                        bytes32 _currency,
                        bytes32 _offerType,
                        bytes32 _isin,
                        string calldata _offeringDocuments
                        ) external {
        require(registrar==msg.sender);
        require(category=="Bond");
        require(VerifiedProducts(productContract).getIssue(productRef)==address(this));
        bool found = false;
        address currencyAddress = ViaFactory(viaFactoryContract).getTokenByNameType(_currency,"ViaCash");
        require(ViaFactory(viaFactoryContract).getType(currencyAddress) == "ViaCash");
        for(uint i=0; i<currencies.length; i++){
            if(currencies[i]==currencyAddress)
                found = true;
        }
        if(!found)
            currencies.push(currencyAddress);
        bonds[currencyAddress].issueSize = _issueSize;
        bonds[currencyAddress].offerPrice = _offerPrice;
        bonds[currencyAddress].minAskPrice = _minAskPrice;    
        bonds[currencyAddress].minSubscription = _minSubscription;    
        bonds[currencyAddress].currency = _currency;
        bondIssue.offerType = _offerType;
        bondIssue.couponPaymentCycle = _couponPaymentCycle; 
        bondIssue.tenure = _tenure; 
        bondIssue.isin = _isin;
        bondIssue.offeringDocuments = _offeringDocuments;
        bondIssue.status = "Issued";
        emit issued (
            _issueSize,
            _offerPrice, 
            _minAskPrice,   
            _minSubscription,
            _currency,
            address(this),
            _offerType,
            _isin,
            "Issued",
            "Bond"
        );
    }

    function getBond() external view returns(Bond.currencySpecificBondAttribs[] memory, Bond.commonBondAttribs memory){
        Bond.currencySpecificBondAttribs[] memory notes = new Bond.currencySpecificBondAttribs[](currencies.length);
        Bond.currencySpecificBondAttribs memory bond;
        for(uint i=0; i<currencies.length; i++){
            bond = Bond.currencySpecificBondAttribs({
                issueSize : bonds[currencies[i]].issueSize,
                coupon : bonds[currencies[i]].coupon,
                offerPrice : bonds[currencies[i]].offerPrice, 
                minAskPrice : bonds[currencies[i]].minAskPrice, 
                minSubscription : bonds[currencies[i]].minSubscription,
                currency : bonds[currencies[i]].currency,
                security : bonds[currencies[i]].security
            });
            notes[i] = bond;
        }
        return (notes, bondIssue);
    }

    function registerPlatform(address _liquidityPlatform) external {
        require(productContract==msg.sender);
        platforms.push(_liquidityPlatform);
    }

    function askOffers() external {
        require(registrar==msg.sender);
        bytes32 isin;
        bytes32 currency;
        uint256 issueSize;
        uint256 offerPrice; 
        uint256 minAskPrice; 
        uint256 minSubscription;
        for(uint i=0; i<currencies.length; i++){
            if(category=="share"){
                isin = stockIssue.isin;
                currency = shares[currencies[i]].currency;
                issueSize = shares[currencies[i]].issueSize;
                offerPrice = shares[currencies[i]].offerPrice;
                minAskPrice = shares[currencies[i]].minAskPrice;    
                minSubscription = shares[currencies[i]].minSubscription;            
            }
            else if(category=="bond"){
                isin = bondIssue.isin;
                currency = bonds[currencies[i]].currency;
                issueSize = bonds[currencies[i]].issueSize;
                offerPrice = bonds[currencies[i]].offerPrice;
                minAskPrice = bonds[currencies[i]].minAskPrice;
                minSubscription = bonds[currencies[i]].minSubscription;
            }
            ISecuritiesRegistry(securityRegistriesContract).createSecurity(VerifiedProducts(productContract).getIssuerAddress(productRef),
                                                        securityContract,
                                                        currency,
                                                        VerifiedProducts(productContract).getIssuerName(productRef),
                                                        isin, "DP");
            security = ISecuritiesRegistry(securityRegistriesContract).getToken(currency,
                                                                                VerifiedProducts(productContract).getIssuerName(productRef),
                                                                                isin);
            ISecurity(security).mint(address(this), issueSize);
            if(category=="share"){
                shares[currencies[i]].security = security;
                stockIssue.status = "Offered";
            }
            else if(category=="bond"){
                bonds[currencies[i]].security = security;
                bondIssue.status = "Offered";
            }
            emit offered(security, "Offered");
            //offer the issue on defi platforms
            for(uint j=0; j<platforms.length; j++){
                IMarketMaker(platforms[j]).make(security, isin, issueSize, currencies[i], offerPrice, minAskPrice, minSubscription);
            }
        }
    }

    function startIssue(uint256 cutOffTime) external {
        require(registrar==msg.sender);
        for(uint i=0; i<platforms.length; i++){
            IMarketMaker(platforms[i]).setRelayer();
            for(uint j=0; j<currencies.length; j++){
                if(category=="share"){
                    IMarketMaker(platforms[i]).issue(shares[currencies[j]].security, cutOffTime);
                    stockIssue.status = "Started";
                    emit opened(shares[currencies[j]].security, "Offered");
                }
                else if(category=="bond"){
                    IMarketMaker(platforms[i]).issue(bonds[currencies[j]].security, cutOffTime);
                    bondIssue.status = "Started";
                    emit opened(bonds[currencies[j]].security, "Offered");
                }
            }
        }
    }

    function getLiquidityProviders(address platform, bytes32 isin) external returns(LP.lp[] memory){
        return IMarketMaker(platform).getLiquidityProviders(isin);
    }

    function getSubscribers() external returns(VerifiedIssues.ioi[] memory) {
        require(registrar==msg.sender);
        for(uint i=0; i<platforms.length; i++){
            for(uint k=0; k<currencies.length; k++){
                address securitytoken;
                if(category=="share")
                    securitytoken = shares[currencies[k]].security;
                else if(category=="bond")
                    securitytoken = bonds[currencies[k]].security;
                (bytes32[] memory pools, bool issueClosed) = IMarketMaker(platforms[i]).close(securitytoken);
                if(issueClosed){
                    if(category=="share")
                        stockIssue.status = "Closed";
                    else if(category=="bond")
                        bondIssue.status = "Closed";
                    emit closed(securitytoken, "Closed");
                }
                for(uint j=0; j<pools.length; j++){
                    IMarketMaker.subscriptions[] memory subs = IMarketMaker(platforms[i]).getSubscribers(pools[j]);
                    for(uint l=0; l<subs.length; l++){
                        uint256 count = subscribers[productRef].length;
                        subscribers[productRef][count].platform = platforms[i];
                        subscribers[productRef][count].poolid = pools[j];
                        subscribers[productRef][count].amount = subs[l].amount;
                        subscribers[productRef][count].price = subs[l].price;
                        subscribers[productRef][count].currency = subs[l].name;
                        subscribers[productRef][count].investor = subs[l].investor;
                        subscribers[productRef][count].asset = subs[l].asset;
                        bool found = false;
                        for(uint x=0; x<investors.length; x++){
                            if(investors[x]==subs[l].investor)
                                found = true;
                        }
                        if(!found)
                            investors.push(subs[l].investor);
                    }
                }
            }
        }
        return subscribers[productRef];
    }

    function allotIssue(bytes32 _allotment, address _platform, bytes32 _pool, address _investor, uint256 _amount, address _asset) external {
        require(registrar==msg.sender);
        timeOfIssue = now;
        if(_allotment=="accept"){
            for(uint i=0; i<subscribers[productRef].length; i++){
                if(subscribers[productRef][i].poolid==_pool &&
                   subscribers[productRef][i].investor==_investor &&
                   subscribers[productRef][i].amount >= _amount &&
                   subscribers[productRef][i].asset == _asset
                   ){
                    IMarketMaker(_platform).accept(_pool, _investor, _amount, _asset);
                    subscribers[productRef][i].status == true;
                    bool found = false;
                    for(uint j=0; j<beneficiaries.length; j++){
                        if(beneficiaries[j]==_investor)
                            found = true;
                    }
                    if(!found)
                        beneficiaries.push(_investor);
                }
            }
        }
        else if(_allotment=="reject"){
            IMarketMaker(_platform).reject(_pool, _investor);
        }
        if(category=="share")
            stockIssue.status = "Allotted";
        else if(category=="bond")
            bondIssue.status = "Allotted";
        emit allotted(address(this), "Allotted");
    }

    function getBeneficiaries() external view returns(address[] memory){
        require(registrar==msg.sender || issuer==msg.sender);
        return beneficiaries;
    }

    function computeNextInstallment(address _currency) external returns(uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        return (beneficiaries.length * bonds[_currency].coupon);
    }

    function getPaymentAmountFor(address _beneficiary) external returns(uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        uint256 installments = BokkyPooBahsDateTimeLibrary.subSeconds(now, timeOfIssue)/bondIssue.couponPaymentCycle;
        uint256 paymentMade = 0;
        for(uint i=0; i<installments; i++){
            paymentMade = paymentMade + payouts[_beneficiary][i].amount;
        }
        return (installments);
    }

    function getPaymentStatusFor(address _beneficiary) external returns(VerifiedIssues.payment[] memory){
        require(registrar==msg.sender || issuer==msg.sender);
        return payouts[_beneficiary];
    }

    function isAllPaidFor(uint256 _byTime) external returns(address[] memory){
        require(registrar==msg.sender || issuer==msg.sender);
        address[] memory unpaidBeneficiaries = new address[](beneficiaries.length);
        uint count =0;
        for(uint i=0; i<beneficiaries.length; i++){
            uint latestPayment = payouts[beneficiaries[i]].length-1;
            if(_byTime > payouts[beneficiaries[i]][latestPayment].payoutDate){
                unpaidBeneficiaries[count] = beneficiaries[i];
                count = count + 1;
            }
        }
        return unpaidBeneficiaries;
    }

    function getDateOfIssue() external returns(uint256, uint256, uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        return (BokkyPooBahsDateTimeLibrary.getDay(timeOfIssue),
                BokkyPooBahsDateTimeLibrary.getMonth(timeOfIssue),
                BokkyPooBahsDateTimeLibrary.getYear(timeOfIssue));
    }

    function getmaturityDate() external returns(uint256, uint256, uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        uint256 maturityDate = BokkyPooBahsDateTimeLibrary.addSeconds(timeOfIssue, bondIssue.tenure);
        return (BokkyPooBahsDateTimeLibrary.getDay(maturityDate),
                BokkyPooBahsDateTimeLibrary.getMonth(maturityDate),
                BokkyPooBahsDateTimeLibrary.getYear(maturityDate));
    }

    function getfirstCouponDate() external returns(uint256, uint256, uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        uint256 couponDate = BokkyPooBahsDateTimeLibrary.addSeconds(timeOfIssue, bondIssue.couponPaymentCycle);
        return (BokkyPooBahsDateTimeLibrary.getDay(couponDate),
                BokkyPooBahsDateTimeLibrary.getMonth(couponDate),
                BokkyPooBahsDateTimeLibrary.getYear(couponDate));
    }

    function computeNextCouponDate() external returns(uint256, uint256, uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        for(uint i=0; i<(bondIssue.tenure/bondIssue.couponPaymentCycle); i++){
            if(BokkyPooBahsDateTimeLibrary.addSeconds(timeOfIssue, (bondIssue.couponPaymentCycle*(i+1)))>now){
                uint256 nextDate = BokkyPooBahsDateTimeLibrary.addSeconds(timeOfIssue, (bondIssue.couponPaymentCycle*(i+1)));
                return (BokkyPooBahsDateTimeLibrary.getDay(nextDate),
                        BokkyPooBahsDateTimeLibrary.getMonth(nextDate),
                        BokkyPooBahsDateTimeLibrary.getYear(nextDate));
            }
        }
    }
    
    function getcouponFrequencyInMonths() external returns(uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        uint256 firstCouponDate = BokkyPooBahsDateTimeLibrary.addSeconds(timeOfIssue, bondIssue.couponPaymentCycle);
        return BokkyPooBahsDateTimeLibrary.diffMonths(firstCouponDate, timeOfIssue);
    }

    function getinterestRateInBips(address _currency) external returns(uint256){
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="bond");
        return bonds[_currency].coupon;
    }

    function payOut(address _beneficiary, bytes32 _currency, uint256 _amount) external {
        require(registrar==msg.sender || issuer==msg.sender);
        require(category=="share" || category=="bond");
        (address token, bytes32 tokentype) = ViaFactory(viaFactoryContract).getAddressAndType(_currency);
        if(tokentype=="ViaCash"){
            if(ViaCash(token).transferFrom(msg.sender, _beneficiary, _amount)){
                VerifiedIssues.payment memory paid = VerifiedIssues.payment({
                    currency : _currency,
                    amount : _amount,
                    payoutDate : now,
                    payer : msg.sender
                });
                payouts[_beneficiary][payouts[_beneficiary].length] = paid;                
            }
        }
    }

}
