// Fetching pre-trade information
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;
 
import "./interfaces/IPreTrade.sol";
import "./interfaces/ISecuritiesRegistry.sol";
import "./interfaces/VerifiedKYC.sol";
import "./interfaces/VerifiedClient.sol";
import "./registry/Security.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "./utilities/StringUtils.sol";
import "./interfaces/ViaFactory.sol";

contract PreTrade is IPreTrade, Initializable {

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    using stringutils for *;

    //mapping of securities approval request to security details
    mapping(bytes32 => security) private securities;

    //array of securities approval requests
    bytes32[] secRefs;
    
    struct registration {
                        address user;
                        bytes32 countryCode;
                        bytes32 dematAccountNo;
                        bytes32 DPID;
                        uint registrationDate;
                        }

    //mapping of demat registration request to Demat account 
    mapping(bytes32 => registration) private registrations;

    //array of registration requests
    bytes32[] regRefs;
    
    address securitiesRegistry;
    address securityToken;
    address kyc;
    address client;
    ViaFactory private via;

    function initialize (address _viaFactory, address _securitiesRegistry, address _securityToken, address _kyc, address _client) public initializer{
        securitiesRegistry = _securitiesRegistry;
        securityToken = _securityToken;
        kyc = _kyc;
        client = _client;
        via = ViaFactory(_viaFactory);
    }

    function registerDematAccount(bytes32 _currencyCode) external {
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        require(via.getTokenByNameType(_currencyCode, "ViaCash")!=address(0x0));
        bytes32 ref = keccak256(abi.encodePacked(msg.sender, now));
        regRefs.push(ref);
        registrations[ref].user = msg.sender;
        registrations[ref].countryCode = _currencyCode.substringinbytes(2,4);
    }

    function getRegistrationRequests(bytes32 _countryCode) external view returns(bytes32[] memory){
        require(_countryCode!="");
        uint entries = regRefs.length;
        bytes32 role = VerifiedClient(client).getRole(msg.sender);
        if(role=="DP"){
            bytes32[] memory result = new bytes32[](entries);
            uint entry=0;
            for(uint i=0; i<regRefs.length; i++){
                if(registrations[regRefs[i]].countryCode==_countryCode){
                    result[entry]=regRefs[i];
                    entry = entry+1;
                }
            }
            return result;
        }
    }

    function getRegistrationRequest(bytes32 _ref) external view returns(address user,
                                                                        bytes32 countryCode,
                                                                        bytes32 dematAccountNo,
                                                                        bytes32 DPID,
                                                                        uint registrationDate){
        require(_ref!="");
        return( registrations[_ref].user,
                registrations[_ref].countryCode,
                registrations[_ref].dematAccountNo,
                registrations[_ref].DPID,
                registrations[_ref].registrationDate);
    }

    function setRegistrationStatus(bytes32 _ref, bytes32 _DPID, bytes32 _dematAccountNo) external{
        require(_ref!="");
        if(VerifiedClient(client).getRole(msg.sender)=="DP"){
            registrations[_ref].DPID = _DPID;
            registrations[_ref].dematAccountNo = _dematAccountNo;
        }
    }

    function registerSecurities(bytes32 _currencyCode, 
                                bytes32 _stype, 
                                bytes32 _isin, 
                                bytes32 _company, 
                                bytes32 _itype,
                                uint _noOfCertificates,
                                uint _faceValue,
                                bytes32 _lockInReason,
                                uint _lockInReleaseDate) external returns(bytes32){
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        require(via.getTokenByNameType(_currencyCode, "ViaCash")!=address(0x0));
        require(_stype=="Free" || _stype=="LockedIn");
        require(_itype=="Equity" || _itype=="Bond" || _itype=="Fund" || _itype=="Units" || _itype=="Certificates");
        bytes32 ref = keccak256(abi.encodePacked(msg.sender, now));
        secRefs.push(ref);
        securities[ref].requestor = msg.sender;
        securities[ref].currencyCode = _currencyCode;
        securities[ref].stype = _stype;
        securities[ref].isin = _isin; 
        securities[ref].company = _company; 
        securities[ref].itype = _itype;
        securities[ref].noOfCertificates = _noOfCertificates;
        securities[ref].faceValue = _faceValue;
        securities[ref].lockInReason = _lockInReason;
        securities[ref].lockInReleaseDate = _lockInReleaseDate;
        securities[ref].registrationRequestDate = now;
        securities[ref].approvalStatus = "Pending";
                                            
        return ref;
    }

    function getConfirmationRequests(bytes32 _countryCode) external view returns(bytes32[] memory){
        require(_countryCode!="");
        uint entries = secRefs.length;
        bytes32[] memory result = new bytes32[](entries);
        uint entry=0;
        bytes32 role = VerifiedClient(client).getRole(msg.sender);
        if(role=="DP"){
            for(uint j=0; j<secRefs.length; j++){
                if( securities[secRefs[j]].currencyCode.substringinbytes(2,4)==_countryCode && securities[secRefs[j]].approvalStatus=="Pending"){
                    result[entry]=secRefs[j];
                    entry=entry+1;
                }
            }
        }
        if(VerifiedKYC(kyc).getStatus(msg.sender)==3){
            for(uint i=0; i<secRefs.length; i++){
                if( securities[secRefs[i]].requestor==msg.sender){
                    if(securities[secRefs[i]].approvalStatus=="Confirmed"){
                        result[entry]=secRefs[i];
                        entry=entry+1;
                    }
                }
            }
        }
        return result;        
    }

    function getConfirmationRequest(bytes32 _ref) external view returns (security memory){
        IPreTrade.security memory response = IPreTrade.security({
            requestor : securities[_ref].requestor,
            currencyCode : securities[_ref].currencyCode,
            stype : securities[_ref].stype,
            isin : securities[_ref].isin, 
            company : securities[_ref].company, 
            itype : securities[_ref].itype,
            lockInReason : securities[_ref].lockInReason,
            approvalStatus : securities[_ref].approvalStatus,
            noOfCertificates : securities[_ref].noOfCertificates,
            lockInReleaseDate : securities[_ref].lockInReleaseDate,
            registrationRequestDate : securities[_ref].registrationRequestDate,
            faceValue : securities[_ref].faceValue, 
            token : address(0x0)
        });
        return response;
    }

    function confirmSecurities(address _user, bytes32 _ref, bytes32 _status) external {
        require(_status=="Confirmed" || _status=="Rejected" || _status=="Pending");
        require(securities[_ref].requestor==_user);
        if(VerifiedClient(client).getRole(msg.sender)=="DP"){
            ISecuritiesRegistry(securitiesRegistry).createSecurity( securities[_ref].requestor,
                                                                    securityToken,
                                                                    securities[_ref].currencyCode,
                                                                    securities[_ref].company,
                                                                    securities[_ref].isin, "DP");
            address _token = ISecuritiesRegistry(securitiesRegistry).getToken(securities[_ref].currencyCode,
                                                                    securities[_ref].company,
                                                                    securities[_ref].isin);
            Security(_token).mint(_user, securities[_ref].noOfCertificates);
            securities[_ref].token = _token;
            securities[_ref].approvalStatus = _status;
        }
    }

    function getSecurity(address _token) external view returns(bytes32, bytes32, address){
        for(uint i=0; i<secRefs.length; i++){
            if(securities[secRefs[i]].token==_token){
                return(securities[secRefs[i]].isin, securities[secRefs[i]].company, securities[secRefs[i]].token);
            }
        }        
    }

    function getDP(address _party) external view returns(bytes32){
        for(uint i=0; i<regRefs.length; i++){
            if(registrations[regRefs[i]].user==_party){
                return (registrations[regRefs[i]].DPID);
            }
        }
    }
    
}
